                        {
                            "type": "element",
                            "tag": "strong",
                            "props": {},
                            "children": [
                                {
                                    "type": "text",
                                    "value": "Limitation :"
                                }
                            ]
                        },
                        {
                            "type": "text",
                            "value": " le sort ne permet pas de se transformer en créature artificielle ou en mort‐vivant."
                        }
